import React from 'react';
import '../styles/MainContent.css';

function MainContent() {
    return (
        <main className="card">
            <h1 className="advice-id">ADVICE #117</h1>
            <p className="advice-text">
                “It is easy to sit up and take notice, what's difficult is getting up and taking action.”
            </p>
            <div className="divider"></div>
            <button className="dice-button">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                    <path fill="currentColor" d="M12 2C6.486 2 2 6.486 2 12s4.486 10 10 10 10-4.486 10-10S17.514 2 12 2zm0 18c-4.411 0-8-3.589-8-8s3.589-8 8-8 8 3.589 8 8-3.589 8-8 8zm-1-13h2v6h-2zm1 8c-.553 0-1-.447-1-1s.447-1 1-1 1 .447 1 1-.447 1-1 1z"/>
                </svg>
            </button>
        </main>
    );
}

export default MainContent;
